import classes from "./DropDown.module.css";

const regions = [{value:"DEFAULT", label: "Select a region"}, {value:"RU", label: "RU"}]
const DropDown = (props) => {
  return (
    <select
      value={props.selectedRegion}
      onChange={(event) => {props.onSaveRegionData(event.target.value)}}
      className={classes.dropDown}
    >
      {regions.map((item) => {
        <option value={item.value}>{item.label}</option>
      })}
      
    </select>
    //add map loop. hav eobject bunch of servers and key of values of keys is lable what is displayed on screen 
  );
};

export default DropDown;
