import SearchForm from "./SearchForm";

var enteredData;

const retrieveData = (props) => {
  const onSearchDataHandler = (data) => {
    enteredData = { ...data };
    console.log(enteredData.user.value);
  };

  const retrieveDataHandler = (summoner_name, region, riotAPIKey) => {
    summoner_name = enteredData.user;
    region = enteredData.region;
    riotAPIKey = "RGAPI-66e7cf60-65b9-4d51-a9cd-5ccadc9cb5e9";

    fetch(
      `https://oc1.api.riotgames.com/lol/summoner/v4/summoners/by-name/${summoner_name}?api_key=${riotAPIKey}`,
      {
        accept: {
          "Content-Type": "application/json",
        },
      }
    )
      .then((response) => response.json())
      .then((json) => console.log(json));
  };

  return <SearchForm onSaveSearch={onSearchDataHandler} />;
};

export default RetrieveData;
