import React, { useRef, useState } from "react";
import DropDown from "../UI/DropDown";
import classes from "./SearchForm.module.css";
import { IconContext } from "react-icons";
import {BiSearchAlt} from "react-icons/bi";


//need to make the data sender in a statet (maybe state is searched data)




const SearchForm = (props) => {
  const userInputRef = useRef();

  const saveRegionDataHandler = (data) => {
    let enteredRegion = data;
  };

  const [enteredData, setEnteredData] = useState("")
  
  const searchSubmitHandler = (event) => {
    event.preventDefault();
    console.log("button pressed");

    const enteredUser = userInputRef.current.value;
    userInputRef.current.value = "";

    const data = {
      user: enteredUser,
      region: enteredRegion,
    };

    setEnteredData(data);

    props.onSaveSearch(data);

    setEnteredData("");
  };

  return (
    <>
      <div>
        <form onSubmit={searchSubmitHandler} className={classes.searchBar}>
          <DropDown onSaveRegionData={saveRegionDataHandler} />
          <label htmlFor="User" />
          <input
            id="user"
            type="text"
            placeholder="Enter a summoner name"
            ref={userInputRef}
          />
          <button type="submit">
            <IconContext.Provider
              value={{ color: "white", size: "30px"}}
            >
              <div>
                <BiSearchAlt />
              </div>
            </IconContext.Provider>
          </button>
        </form>
      </div>
    </>
  );
};

export default SearchForm;
