import SearchForm from "./SearchForm";



const RetrieveData = (props) => {
  const onSearchDataHandler = (data) => {
    const enteredData = { ...data };
    let summonerId;
    let teamId;
    const summonerName = enteredData.user;
    const region = enteredData.region;
    const riotAPIKey = "RGAPI-66e7cf60-65b9-4d51-a9cd-5ccadc9cb5e9";

    //Get summoner ID
    fetch(
      `https://oc1.api.riotgames.com/lol/summoner/v4/summoners/by-name/${summonerName}?api_key=${riotAPIKey}`,
      {
        accept: {
          "Content-Type": "application/json",
        },
      }
    )
      .then((response) => response.json())
      .then((json) => summonerId = json["id"])
      .then(() => console.log(summonerId));

    // Get clash team ID
    fetch(
        `https://oc1.api.riotgames.com/lol/clash/v1/players/by-summoner/${summonerId}?api_key=${riotAPIKey}`,
        {
          accept: {
            "Content-Type": "application/json",
          },
        }
      )
        .then((response) => response.json())
        .then((json) => teamId = json[0]["teamId"])
        .then(() => console.log(teamId));
    

  };

  return <SearchForm onSaveSearch={onSearchDataHandler} />;
};

export default RetrieveData;
